INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'7', N'普通高等教育“十一五”国家级规划教材：体育游戏                            ', N'基本半导体器件、放大电路基础、集成运算放大电路、反馈放大电路、波形发生电路、直流稳压电源、数字电路基础、组合逻辑电路、触发器和时序逻辑电路、脉冲电路及模数转换。《电子线路》由华中科技大学陈大钦教授审稿。《电子线路》可作为高等学校理科物理专业电子线路课程的教材，也适用于高等院校电子线路课时较少的各类专业教学使用，还可供从事电子线路工作的工程技术人员参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ')
