INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'17', N'支挡结构设计                                             ', N'《土力学与地基基础（第2版）》为普通高等教育“十一五”国家级规划教材。全书内容共分十一章，并根据《建筑地基基础设计规范》（GB50007-2002）、《岩土工程勘查规范》（GB50022001）及《建筑地基处理技术规范》（JGJ792002）等规范要求系统地阐述了土的物理性质与工程分类、土中应力计算、地基变形计算、土的抗剪强度与地基承载力、土压力与土坡稳定、岩土工程勘察、天然地基上的浅基础设计、桩基础及其他深基础、基坑工程、地基处理、特殊土地基及山区地基等内容。每章正文之前有学习目标，每章正文之后有小结、思考题、习题。《土力学与地基基础（第2版）》精选内容，实用性强，可作为高职高专、成人高校及本科院校所属二级职业技术学院的土建类房屋建筑工程专业的课程教材，也可作为土建类相关专业的课程教材，同时可供土建类专业勘察、设计和施工人员参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ')
