INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'18', N'全国高等学校医学规划教材?急诊医学                                  ', N'《西方哲学史讲演录》是一部“西方哲学史”通识课程的教材，在课堂录音的基础上整理成稿。《西方哲学史讲演录》从古希腊罗马哲学一直讲到黑格尔哲学，系统地阐述了西方哲学发生、发展的历史过程和逻辑脉络。全书思路清晰、内容丰富、文字清新简洁，深入浅出地展示了西方哲学史的精神内涵和演进历程。《西方哲学史讲演录》作者赵林教授是国家教学名师，主讲“西方哲学史”课程达20年之久，该课程于2006年被教育部评为国家级精品课程。《西方哲学史讲演录》充分展现了赵林教授的深邃思想和雄辩口才，堪称一部“西部哲学史”的上乘入门教材。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ')
