INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'23', N'普通高等教育“十一五”国家级规划教材?普通高等教育精品教材：市场营销                 ', N'《经济法习题与案例集》是普通高等教育“十一五”国家级规划教材（高职高专教育）《经济法》（第三版）的配套习题与案例集。与主教材对应，本习题与案例集共分18章，题型包括名词解释、判断题、单项选择题、多项选择题、填空题、简答题、论述题和案例分析题。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ')
