INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'24', N'机床数控技术及应用                                          ', N'《运输管理实务》是普通高等教育“十一五”国家级规划教材。《运输管理实务》把培养物流专业学生与相关从业人员的实际操作能力作为主旨，介绍了运输管理的新理念、新技术和新方法。全书共九章，主要内容包括：运输管理基础知识、公路货物运输、水路货物运输、铁路货物运输、航空货物运输、管道运输、多式联运、货物运输保险与合同、运输决策与管理等方面的知识。《运输管理实务》适用于普通高等院校（高职高专、应用型本科）、成人高校、民办高校及本科院校举办的二级职业技术学院物流及其他相关专业的教学，也可供五年制高职学生使用，并可作为社会从业人士的参考读物。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ')
