INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'45', N'数学史通论（双语版）                                         ', N'《病理生理学习题集》主要依据中南大学肖献忠教授主编、高等教育出版社出版的普通高等教育“十一五”国家级规划教材《病理生理学》（第2版）内容编写，并参考了其他具代表性的病理生理学教材和题集。习题题型包括选择题（单项选择题和多项选择题）、名词解释、问答题和病例分析，附有答案，并编入部分编委所在学校近年来在本科生考试中实际使用的病理生理学试卷及其答案15套。《病理生理学习题集》适合于病理生理学教师备课、辅导、命题及学生复习考试时使用，亦可供相关临床和基础专业人员学习病理生理学知识以及研究生考研时参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ')
