INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'47', N'病理生理学习题集                                           ', N'《建筑概论》精选建筑设计学科的主干教学内容，从建筑内涵与范畴、建筑形态与美感的构成、建筑设计方法、国内外古典建筑艺术等方面进行了具体而深入地分析，同时，对世界建筑发展过程中的主要建筑分支、建筑类型与风格也作了概括性地阐述。由于建筑艺术包涵内容非常广泛，《建筑概论》以系统理论和实际案例为两大核心内容，对建筑内涵及影响建筑设计内容的各个方面进行了系统分类，分别叙述；同时，以具体案例的分析为学生提供理解与认识建筑、了解建筑设计基本知识和专业结构的基本知识框架。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ')
