INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'48', N'All-Start全明星英语1（教师参考书）                             ', N'《普通高等教育“十一五”国家级规划教材?高等数学及其应用（下）》适用于应用型人才培养中的高等数学教学，内容繁扣教育部数学与统计学教学指导委员会制定的本科数学基础课程教学基本要求，突出微积分的应用，在保持数学内容的系统性和完整性的前提下，适当降低了某些内容的理论深度，加强对微积分中有重要应用背景概念、理论、方法和实例的介绍。在文字表述上做到详尽通畅，浅显易懂。选配的习题突出基本数学能力的训练而不追求技巧，使教材易教易学、方便自学。书中有些内容加了“*”号或用小字印刷，便于教师灵活掌握。《高等数学及其应用(下                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          ')
