INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'63', N'酒庄设计                                               ', N'《有机化学》是教育部普通高等教育“十一五”国家级规划教材，国家精品课程教材建设和高等教育出版社“高等教育百门精品课程教材建设计划”立项研究项目的研究成果。《有机化学》是在原版的基础上，结合教学改革与实践，并广泛征求使用学校意见后而修订的。全书分为7篇21章：第1篇有机化学概论；第2篇烃及卤代烃；第3篇有机含氧化合物；第4篇有机含氮化合物及杂环化合物；第5篇天然有机化合物；第6篇有机合成基础及绿色有机合成；第7篇有机化学教学资源。章后有习题，书后有索引和部分参考书目。与《有机化学》配套的辅助教材还有《有机化学学习指导》、《有机化学电子教案》和《有机化学教学软件》等。《有机化学》可作为化学工程与工艺、制药工程、生物化工和应用化学等专业教材，也可供相关专业选用和参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          ')
