INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'76', N'数学翻译丛书：Markov过程导论                                  ', N'本书主要论述透明导电薄膜的性能与技术，比较系统全面地介绍了透明导电氧化物薄膜的结构、性能、制备、表征与应用，反映了当前透明导电氧化物薄膜性能与技术研究、发展的前沿信息。全书共七章，包括了透明导电氧化物薄膜的基本特征、沉积制备、性能分析检测、微细结构、电学性能、光学性能及应用等内容。书中不仅包含作者近年来在国内外的最新研究成果，还概括了国内外很多学者在透明导电氧化物薄膜研究上的主要成就，是本比较系统全面的透明导电氧化物薄膜论著。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  ')
