INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'86', N'全国计算机等级考试2级教程：Java语言程序设计（2008年版）                   ', N'《电动力学（第3版）》是作者在所编《电动力学》（1997年第二版）的基础上，根据学科的发展和教学实践的需要修订而成的。这次修订，在保持原书整体结构精炼、严谨，叙述简明、流畅，便于教学的特色下，改写了部分内容，新增了部分内容，力求做到既重视基本理论，又扩展学生视野，引导学生关注学科前沿的发展动态，训练学生提出问题和解决问题的能力，激励学生的创新精神。《电动力学》共分7章，内容包括：电磁现象的普遍规律、静电场、静磁场、电磁波的传播、电磁波的辐射、狭义相对论、带电粒子和电磁场的相互作用。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ')
