INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'95', N'新航道?新托福口语真经                                        ', N'本丛书精选对人类文明发展起过重要作用、在深化人类对世界的认识或推动人类对世界的改造方面有某种里程碑意义的主题，深入浅出地介绍数学文化的丰富内涵、数学发展史中的一些重要篇章以及一些著名数学家的历史功绩和优秀品质等内容，适于包括中学生在内的读者阅读。《二战时期密码决战中的数学故事》是其中之一！                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ')
