INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'117', N'基本设计：智性理性和感性的孕育                                    ', N'《全国计算机等级考试二级教程：Accss数据库程序设计（2010年版）》是在教育部考试中心组织下，依据2007年版《全国计算机等级考试二级Accss数据库程序设计考试大纲》编写，内容包括数据库基础知识、Accss的基本操作和编程等，简明扼要，理论联系实际。《全国计算机等级考试二级教程：Accss数据库程序设计（2010年版）》是全国计算机等级考试二级Accss数据库程序设计的指定培训教材，同时也可以作为其他人员学习Accss2003的教材和参考用书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ')
