INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'124', N'高等学校教材：高等电磁理论（（修订版）                                ', N'《网站开发与管理（第2版）》在第一版的基础上，对大部分内容进行了修订与重新编写，使其更加体现网站开发与管理的最新理论与应用技术。全书共分7章，从网站建设的规划与流程开始，全面介绍网站开发与管理的基础知识、网页设计的基本内容与制作技术、网站管理的基本原则与发布技术，并结合电子商务等行业介绍在网站开发与管理方面具有特色的典型案例。《网站开发与管理（第2版）》既可作为工商管理类专业的教材，也可供进行网站开发与管理的相关人员参考和使用。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 ')
