INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'130', N'随机金融基础：理论（第2卷）                                     ', N'《全国计算机等级考试三级教程：网络技术（2010年版）》根据教育部考试中心最新颁布的《全国计算机等级考试三级网络技术考试大纲（2007年版修订版）》的要求，在2008年版的基础上修订而成。主要内容包括：计算机基础、网络技术基础、局域网基础、服务器操作系统、Inn基础、Inn基本服务、网络管理与网络安全、网络应用技术。在《全国计算机等级考试三级教程：网络技术（2010年版）》的最后还附有最新版的考试大纲，2008年4月全国计算机等级考试三级笔试试卷网络技术和答案及评分参考、各章习题参考答案。《全国计算机等级考试三级教程：网络技术（2010年版）》的修订目标是使三级网络技术的考生具有计算机基本知识，了解和掌握局域网、Inn的基本原理，熟悉计算机网络系统的组建方法和技术，从而具备简单网络应用系统使用与维护的基本能力。《全国计算机等级考试三级教程：网络技术（2010年版）》可供报考全国计算机等级考试三级网络技术的考生使用，也可作为普通高等学校网络课程的教材使用。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ')
