INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'137', N'高等学校旅游管理专业课程教材：模拟导游实务英语教程                          ', N'《田径训练教程》依据全国普通高等学校运动训练专业学科课程的内容和要求编写而成.《田径训练教程》共八章，包括田径训练概述，走、跑，跳跃，投掷，教学与训练，体能训练，疲劳的诊断与恢复以及规则裁判等内容。在编写的过程中，作者借鉴和汲取了我国关于田径教材最新的研究内容，注意将国内外先进的田径训练理论和方法与我国田径运动训练实践相结合，力图从科学的高度反映我国现代田径训练理论的最新研究成果。从多视角、多方位地介绍先进的、有效的体能训练手段与方法，重点突出能力的培养和提高，以满足专业学生的实际需要。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ')
