INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'139', N'田径训练教程（附光盘）                                        ', N'《大学生职业发展与就业指导》涵盖了大学生职业规划与求职就业的全过程，共由四部分组成。第一部分概论，介绍了与大学生职业发展有关的各种概念和基本过程；第二部分为职业发展规划，由第二、三、四、五章构成，系统地介绍了职业发展规划中自我探索、认识职业、职业决策和提升就业能力等各主题的理论与实务，能够让大学生对职业发展规划的概念有清晰的认识，很好地掌握规划职业发展的方法；第三部分为求职择业指导，由第六、七、八、九章组成，详细地介绍了大学生求职择业各个环节的主要内容，从求职准备、笔试、面试、政策法规与权益保护到为人职做准备，内容丰富翔实；最后一部分为创业，由第十、十一两章组成，对大学生创业的相关问题做了简要的阐述，重在激发同学们的创业意识，同时也介绍了创业的基本过程和要求。《大学生职业发展与就业指导》体系完整，素材丰富，立足于学生需要，强调实用性与操作性，适合普通高校各年级学生公共课选用。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ')
