INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'141', N'全国高等学校教材：大学生职业发展与就业指导                              ', N'《奇异摄动方程解的渐近展开》是利用作者А．Б．瓦西里耶娃在20世纪60年代提出的“边界层函数法”，对奇异地依赖于小参数的常微分方程组、积分―微分方程组和时滞微分方程组等各种非线性系统定解问，题进行近似求解和渐近分析的专著。其特点是系统地论述该方法的理论基础和运用该方法对各种问题的渐近解进行构造的过程，而且对定理、命题和结果都给出详细的推导和论证，是一本关于这类非线性微分方程组奇异摄动问题的基本理论著作。《奇异摄动方程解的渐近展开》适合于从事渐近方法的研究生、大学生、应用数学工作者以及需要处理各种非线性奇异摄动方程组数学模型的科技工作者，对于需要求解非线性方程组的物理、力学和工程技术人员也是一本有用的参考书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ')
