INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'145', N'高等学校教材：全民健身导论                                      ', N'《客户关系管理》是普通高等教育“十一五”国家级规划教材。《客户关系管理》立足于高等职业教育的特点，从企业实际应用出发，较系统地阐述了客户关系管理的基本应用技术。书中主要讨论了客户关系管理的基本概念、客户关系、客户满意度指数、客户忠诚、客户终生价值、客户生命周期、客户关系管理系统的基本组成与分类以及客户关系管理中的数据仓库与数据挖掘技术等。《客户关系管理》注重理论联系实际，叙述时力求深入浅出，简单易懂，并结合大量的实际案例加以说明。《客户关系管理》适用于普通高等院校(高职高专、应用型本科                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ')
