INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'152', N'全国计算机等级考试一级WPS Office教程（2008年版）                    ', N'《大学英语六级考试710分新题型高分攻略：听力分册》是为了帮助考生了解改革后的六级考试大纲对听力的要求，有针对性地进行复习而编写的。编者在仔细研读了近几次的新题型考题后，根据考生反馈的信息，考虑到考生的实际需要和现实水平，编写了此书。全书分析新六级听力在题型、难度、选材等方面的特点，针对不同题型和测试要求进行应试指导，从分项训练入手，从较短会话和段落开始，使考生由浅入深、循序渐进，通过高密度、高强度集中练习某一题型，尽快熟悉其答题思路和方法，掌握考试要点；归纳并总结各种题型的答题规律，适应考试，并逐步达到熟能生巧、各个击破、全面提高的效果。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ')
