INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'156', N'团体咨询：策略与技巧                                         ', N'《叙事学导论》致力于对叙事学基本理论作系统的探讨，力图较为完整地描述出这一理论的基本框架，对涉及叙事学的一系列重要问题，诸如叙事框架的构建、叙述交流过程、叙述声音、叙述聚焦、叙述时间、人物结构模式与人物描绘等，都在理论与实践相结合的基础上，力图作出合理的阐述，以提供切入叙事学的基本途径，并运用这一理论进行实践分析。同时，《叙事学导论》从理论发展的角度出发，对叙事学的发展作动态的描述，在以经典叙事学的探讨作为基础的情况下，对由经典叙事学向后经典叙事学的拓展以及后经典叙事学语境下的研究均作了必要的说明，而且在关注文本与语境相互关系的基础上，通过对一系列不同作品的具体分析，对后经典叙事学的研究作进一步的补充。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ')
