INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'159', N'公共事业管理学（第2版）                                       ', N'《西方经济学》进一步深化了教育部“高等教育面向21世纪教学内容和课程体系改革计划”的研究成果，是面向21世纪课程教材的再修订版。《西方经济学》被列入普通高等教育“十一五”国家级规划教材项目。《西方经济学》准确、简明、通俗、全面地诠释了西方经济学的基本原理和方法，着重反映西方经济学的最新成果和重大进展，并对西方经济学的理论和观点进行必要点评，以引导学生正确掌握和运用西方经济学的原理和方法。《西方经济学》以第二版为基础，针对我国高等院校西方经济学教学的特点和要求，在体例、结构和内容等方面进行了充实和更新，特别适合高校经济、管理类本科生以及自学者学习使用。《西方经济学》配有适合教师使用的教学课件。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              ')
