INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'173', N'现代秘书心理学（第2版）                                       ', N'《管理科学研究方法》是作者长期从事管理科学研究、教学和指导研究生的大部分经验和创新的总结。这些创新和独特经验的总结包括：对管理科学及有关社会科学的研究对象和理论构成的概括；对管理科学研究的三个方面（新时空上的现状、变量关系、对策）的概括；对理论发展与客观世界的四种相互关系的概括；对管理科学研究的六条主要路径的概括；对从假设到证实／证伪的基本过程的概括，特别是对假设来源和假设层次的总结等。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ')
