INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'182', N'培训培训师：TTT指南                                        ', N'《无限维空间上的测度和积分:抽象调和分析》系统地总结了作者和国内外数学家在无限维空间上测度和积分论研究中所得到的某些结果，部分尚属初次发表，全书包括六章：测度论的某些补充知识，正泛函与算子环的表示，具拟不变测度的群上调和分析，线性拓扑空间上的拟不变测度及调和分析，Gass测度，Bs―Ensn场交换关系的表示，另有两个附录，介绍阅读《无限维空间上的测度和积分:抽象调和分析》所需的一些知识，《无限维空间上的测度和积分:抽象调和分析》供高等学校数学系高年级学生、研究生及这方面的数学工作者、理论物理工作者参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           ')
