INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'185', N'新大学日语标准教程（提高篇）（2）（附光盘）                             ', N'《机械原理学习指南（第5版）》是配合西北工业大学机械原理及机械零件教研室编《机械原理》（第七版，普通高等教育“十五”国家级规划教材）的辅助教材。《机械原理学习指南（第5版）》对各章内容的学习要求、重点难点、知识脉络、学习方法和注意事项作了简要说明，并对一些较为典型的例题和问题进行了较为详细的分析和求解，对课程中某些重要问题作了适当拓展延伸。在编写中注意了内容的启发性、科学性、实践性和趣味性。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ')
