INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'188', N'北京英语水平考试 标准教程学习指导（第1级）                             ', N'高教版《系统解剖学》教材自2003年出版发行以来，以其图文并茂、贴近临床、印制精美等特点受到广大医学院校师生的欢迎。本教材与《系统解剖学》配套，从编写体例到编写风格与《系统解剖学》相映，内容上着重论述正常人体局部结构、层次和毗邻关系，以尸体解剖操作为主线，理论描述与解剖操作紧密结合，突出局部解剖学与临床的密切联系，注重实用性。本教材适用于医学各专业本科生局部解剖学教学，同时也可作为临床研究生局部解剖学教材。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ')
