INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'193', N'北京英语水平考试同步训练（第1级）                                  ', N'《美术教育学新编》共分入门、基础、拓展三个部分，入门――唤起记忆和进行体验开始课程的学习；基础――向学习者提供完成美术教学活动最基本的知识与技能；拓展――全面而深入地带领学习者学习美术教育知识与技能，推进学习者向职业高峰发展。《美术教育学新编》不仅涉及了美术教育的基本问题，还以“词语解析”、“拓展阅读”、“案例点击”等栏目呈现了大量相关知识，二者形成了既相并列又相交集的双轨。《美术教育学新编》全书体现出独特的编写思想和当代教科书的编写趋势，符合学生在本专业学习的认知规律，将本学科的学理、内容和方法以通俗易懂、图文并茂的方式加以呈现，融基础性、进阶I生、拓展性为一体而成就自身之特色。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ')
