INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'197', N'国外优秀生命科学教材译丛：生理学原理（第4版）（中文版）（附光盘）                  ', N'《趣味游戏走迷宫》为“游戏走迷宫”系列之一。本套丛书其收集整理了100个适合家庭成员其同参与的小游戏，既有传统的，也有现代的，并详细讲解了不同的玩法，让孩子在亲子游戏中体验快乐，同时也使孩子的大脑功能得到开发。“走迷宫”和“大搜索”是这套游戏书的另外两种玩法。这两种游戏可以促使孩子集中注意力，培养他们的判断、推理以及眼和手的协调能力。怎么样？快陪孩子一起玩吧，您的孩子一定会越来越聪明！世界著名物理学家、日本首席脑科学家小泉英明指导。中国关心下一代工作委员会幼儿体操促进会副秘书长李晓惠博士强力推荐。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              ')
