INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'221', N'益智游戏走迷宫                                            ', N'《全国高等学校药学类规划教材:药理学》遵循科学性、系统性、先进性、逻辑性和实用性的编写宗旨，并努力贯彻“少而精”的原则，重点介绍药理学的基本理论、基本知识和基本技能，内容上力求系统、全面、翔实、新颖。鉴于当今生物医药科技的飞速发展，药理学知识不断更新，《全国高等学校药学类规划教材:药理学》较充分反映了药理学的新理论与重要的新药。根据药学专业学生的培养要求，《全国高等学校药学类规划教材:药理学》注重与新药研发相关学科基础知识和具有启发意义的药物发展简史的介绍，以拓展学生的知识领域。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       ')
