INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'222', N'生态学实验设计与分析（第2版）（中文版）                               ', N'《高级管理学》（第2版）（经济学、管理学类）首先介绍了学习管理学的目的、管理的环境问题、管理的社会责任、德鲁克的目标管理，以及管理创新等基本知识，然后分四篇系统阐述了管理的计划职能、组织职能、领导职能、控制职能四大核心职能的有关理论和方法，最后还探讨了创业管理、风险管理、知识管理的软管理等较前沿的管理学理论。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              ')
