INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'233', N'财务会计                                               ', N'《产业经济学》主要从产业组织、产业结构、产业政策和综合应用等方面系统阐述产业经济学，体系完整，结构安排合理。同时，《产业经济学》重视参考国外同类教材的优点，更注重教材的实用价值，力求体现中国特色。《产业经济学》及其教学资料提供了大量实际案例与习题，便于教师在教学过程中结合案例进行课堂讲授，使学生在掌握基本理论的基础上，又能运用所学的基本理论对实际问题进行分析，以提高学生分析问题的能力。另外，通过各章的习题可以帮助学生融会贯通和开拓思路。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ')
