INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'243', N'图像融合：理论与应用                                         ', N'PETS2级和4级考试的标准被分别用来改造当前高考和研究生入学考试中的英语考试，PETS2级和3级考试被分别用来替代高等教育自学考试中的专科和本科考试中的公共英语考试，而PETS5级考试已替代全国外语水平考试中的英语考试（WSK?EPT）。PETS2级听力考试与高考英语听力无论在内容、形式和难度上都非常相似，《PETS-2听力试题分类解析和高分训练》完全可作为高考听力训练用书。《PETS-2听力试题分类解析和高分训练》针对高考英语听力采用PETS2级听力试题进行全省统考的广大考生编写，为高考英语考生和PETS2级考生提供考前实战训练，有效提高高考入学竞争力和PETS2级听力水平，从而提高英语听力综合能力。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       ')
