INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'248', N'多孔介质污染物迁移动力学                                       ', N'《政治经济学（第3版）》是“面向21世纪课程教材”《政治经济学》的第三版，是普通高等教育“十一五”国家级规划教材。《政治经济学（第3版）》在体系上突破了资本主义两阶段和政治经济学分资本主义、社会主义两部分的传统理论框架，在范畴上突破了政治经济学诠释概念一律问姓“资”、姓“社”的传统分析方法，在内容上突破了只限于对经济制度和生产关系本质的研究，重视对所有制实现形式、生产关系现象形态、市场经济运行和经济发展方式的研究，从本质关系和现象形态两个层面阐述经济制度、经济运行和经济发展规律。全书按照商品经济篇、经济制度篇、经济运行篇和经济发展篇的顺序建立篇章结构和体系。《政治经济学（第3版）》根据党的十七大报告精神，对相关的具体内容进行了必要的修订，以反映我国政治经济学理论与实践的最新成果。《政治经济学（第3版）》适合作为高等学校经济管理类各专业的教学用书，也适合作为自学考试、函授等成人高等教育的参考用书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ')
