INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'252', N'制度经济学                                              ', N'《外汇理论与交易原理》将视野置于经济全球化背景下，研究现代汇率理论与外汇市场演变和发展规律，探讨汇率决定及其对宏观经济变量的影响。《外汇理论与交易原理》吸收了国内外有关汇率理论研究的最新成果和国际金融市场中外汇交易实务的最新变化，结合作者多年从事国际金融学相关课程教学的经验，遵循教材编写的前瞻性、科学性、实用性的原则，在充分考虑与国际接轨的基础上，结合我国外汇体制改革和外汇市场开放的实际情况，分理论篇、实务篇和管理篇详细阐述了外汇的基本原理和外汇交易的实际操作。《外汇理论与交易原理》是继作者主编的《国际金融》荣获国家级精品课程教材后出版的又一部高质量精品教材，既可供高校经济管理类各专业本科生、研究生(含MBA                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     ')
