INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'260', N'全国计算机等级考试二级教程：Delphi语言程序设计（2010年版）                 ', N'本书是按照教育部考试中心颁布的《全国计算机等级考试四级软件测试工程师考试大纲（2007年版）》的有关要求，由教育部考试中心组织，在全国计算机等级考试委员会指导下，邀请有关专家编写的。内容包括软件测试的基本概念、软件测试在软件开发过程中的地位、代码走查与评审、结构覆盖（白盒）测试、功能（黑盒）测试、单元测试和集成测试、系统测试、软件性能测试和可靠性测试、面向对象软件测试、W应用软件测试以及兼容性测试、构件测试、极限测试和文档测试等。本书还对软件测试过程和管理进行了详细阐述，讨论了了软件自动化测试技术和有关工具，介绍了我国软件测试的现行标准和测试文档规范，最后结合软件测试过程管理平台QEs和软件分析与测试工具QESATC介绍了软件测试工程的实践经验。本书内容丰富翔实，理论和实践并重，实用性强，不仅可作为四级软件测试工程师考试的标准教材，而且可用作高校计算机与软件工程专业的教材，也可作为软件测试人员实用的培训教材和技术参考书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         ')
