INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'261', N'全国计算机等级考试四级教程：数据库工程师（2010年版）                       ', N'《毕业论文写作与规范》教材为浙江省教育厅重点教材选题项目成果，2005年经浙江省教育厅批准为省级重点教材。毕业论文（设计）是审核学生毕业与学位资格的重要依据，也是实现高校培养目标的一个重要的教学环节。目前，我国很多高等学校都开设了毕业论文（设计）选修课程，但多是根据本学科专业特点，以讲义的形式授课，能够对毕业论文设计与完成起到综合指导作用的教材非常少。为了解决该问题，本教材力求在把握各类毕业论文写作共性的基础上，以毕业论文（设计）写作流程为线索，对包括选题、资料搜集、研究方法、起草与修改、技术规范、学术规范、发表及答辩在内的各个环节进行系统地介绍和阐述，兼顾文理，并辅以必要的范例，以提高本教材的实用性与普及性。《毕业论文写作与规范》教材适合作为高等院校本科生、研究生及各类自学者的毕业论文（设计）写作参考书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ')
