INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'276', N'国家精品课程主讲教材：计算机组成原理（附光盘1张）                          ', N'《小学英语活动设计与教学》是全国中小学教师远程非学历培训课程资源开发项目研究的成果，书中为读者设计和分析了大量案例，致力于帮助小学英语教师解决教学过程中活动设计与教学存在的问题与困难。《小学英语活动设计与教学》共五个单元，主要内容包括：小学英语活动设计与教学的理论基础、小学英语语言学习活动设计与教学、小学英语语言运用任务活动设计与教学、小学英语兴趣活动设计与教学、小学英语课堂教学各阶段活动设计与教学。《小学英语活动设计与教学》适合小学英语教师、教研员、英语课程研究人员阅读参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ')
