INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'299', N'高等学校教材：FPGA设计基础                                    ', N'《艺术概论》是以本体论为核心建构的艺术理论基础教程，在孙美兰主编的1989年版《艺术概论》基础上作了修订。理论阐发有所深化和拓展，以美术、音乐为主线，扩及文学、戏剧、舞蹈等多门类艺术，力求在中、西艺术比照中探索艺术新观念，并建立艺术理论教程新体系。书中精选典型图例，附有图表。《艺术概论》主要面向高等院校有志提高理论修养的本科生、研究生，并可为不同艺术专业的师生个人进修使用。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ')
