INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'314', N'当代大学生就业研究                                          ', N'《高等学校教材?微积分（经管类）（上册）》是南开大学滨海学院、北京航空航天大学北海学院、天津大学仁爱学院、大连理工大学城市学院等十几所院校根据目前独立学院教学现状，结合多年在独立学院的教学经验联合编写而成。《高等学校教材?微积分（经管类）（上册）》分为上、下册，上册主要内容有：函数、极限与连续，导数与微分，微分中值定理与导数的应用，不定积分，定积分及其应用。书中每节配有A、B两套习题，并附有习题答案。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               ')
