INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'317', N'普通高等教育十一五国家级规划教材：英语同声传译教程                          ', N'《马克思主义基本原理概论学生辅学读本》（修订版）是马克思主义理论研究和建设工程重点教材《马克思主义基本原理概论》（2008年修订版）的配套用书，根据党的十七大精神，进行了相应内容的修改。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ')
