INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'318', N'《思想道德修养与法律基础》学生辅学读本（修订版）                           ', N'《英语口译基础教程》共14个单元含盖了“口译记忆、口译笔记、数字口译、演讲技巧、跨文化交际”等主要交替传译技能，同时包括了“旅游、会展、经济合作、国际商务、礼仪祝辞、表演艺术”等专题在内的多个专题供口译实际操作，强化口译技能的掌握。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     ')
