INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'324', N'物理学与人类文明十六讲                                        ', N'《数字电视设计原理》系统地介绍了数字电视设计和中国数字电视地面广播传输标准GB20600-2006的详细原理与关键技术，对与标准相关的音视频信源编解码技术、信道编解码技术、数字调制与解调技术、信道估计与均衡技术、数字电视系统与主要设备的设计、数字电视基带处理器集成电路芯片的设计、数字电视系统的测试方法以及数字电视的技术发展进行了系统的论述。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              ')
