INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'328', N'矫正社会工作                                             ', N'《英语》系列教材为普通高等教育“十一五”国家级规划教材，供高职高专英语专业使用。《综合教程》是这套系列教材的主干教材之一，分为基础阶段和专业阶段，其中基础阶段共有3册，专业阶段1册。《英语综合教程》为第三册，适用于高职高专各英语专业(包括应用英语专业、商务英语专业、旅游英语专业和英语教育专业等                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ')
