INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'329', N'行为医学                                               ', N'《汽车英语》从专业人员的实际工作需要出发进行设计和编写。选材新颖、结构合理、内容丰富、语言规范；练习兼具实用性和针对性。专门用途英语系列教材是教育部规划的高等学校（包括本科院校、高等专科院校和高等职业院校）专业英语阶段的英语教材，也可供电大、各类成人院校以及广大专业人员学习专业英语、提高涉外业务交际能力使用。《汽车英语》是该系列教材之一，全书由10个单元组成，每单元包括专业文献阅读与翻译、涉外业务应用文模拟套写和专业会话3部分。书后附有练习参考答案和课文参考译文。《汽车英语》书后附MP3。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  ')
