INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'330', N'英语综合教程3（高职高专英语专业适用）                                ', N'TOEFLBT新托福一遍通系列针对美国ETS推行的新型TOEFLInn-BasTs，为考生提供最接近真实考题的考前模拟训练。考生可通过本系列听、说、读、写的综合实战演练，熟悉新托福的考试形式、考试范围和考题难度，熟练掌握应考技巧，轻松取得TOEFLBT高分。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ')
