INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'343', N'遗传学                                                ', N'《高中地理新课程教学设计与评析》是走进课堂――普通高中新课程案例与评析丛书之一。主要内容是针对高中地理新课程教学内容所设计的教学案例。案例结构包括：教材分析与学情分析、教学目标确定、教学重点和难点解析、教学方法选择、教学过程设计等板块。案例内容涉及：太阳对地球的影响、地球运动、大气运动、锋与天气、气压带和风带、自然界的水循环、海水的运动、水资源利用、山岳的形成、河流地貌、自然地理环境、城市化、城市内部空间结构、农业生产与农业区位选择、家乡的农业园区、工业区位因素与区位选择、地理环境对区域发展的影响、荒漠化的防治、森林的开发和保护、西气东输，以及家乡木雕工艺的发展等主题。作者力图通过31个来自课改一线教师的鲜活教学案例，引发对新课程教学中实际问题的研究和思考。《高中地理新课程教学设计与评析》适合作为高中新课程试验区学科教师培训教材，也适合关注高中课程改革的教研人员阅读参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  ')
