INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'348', N'国外优秀生命科学教学用书：生态学（第4版）（影印版）（附光盘）                    ', N'本书是高等学校金融学专业课程教材，是在第一版的基础上根据读者反馈的宝贵意见和作者进一步研究的体会修订、更新而成的。本书内容包括：投资银行概论、证券发行与承销、企业并购、风险投资、证券投资基金、资产证券化、投资银行业监管。书后附有投资银行领域的相关词汇解释，便于读者查阅学习。本次修订增加了一些新内容、新案例、新理论，力求反映投资银行领域的最新理论和实践成果。本书可作为普通高等学校金融、工商管理等专业投资银行学课程教材，也可作为银行、证券、保险、上市公司等相关金融机构高级职员培训用书。本书配有教师教学课件，教师可凭本书最后教学支持说明页获取。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         ')
