INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'357', N'思想道德修养与法律基础教师参考书（修订版）                              ', N'《马克思主义基本原理概论教师参考书》（修订版）是马克思主义理论研究和建设工程重点教材《马克思主义基本原理概论》（2008年修订版）的配套用书。《马克思主义基本原理概论教师参考书》（修订版）根据党的十七大精神，并紧紧依托教材，在认真研究课程教学内容和教学规律的基础上修订而成。《马克思主义基本原理概论教师参考书》（修订版）仍按教材的编写章节来进行设计，每章包括教学目的、内容概述、教学重点、讲授建议、背景资料、经典论述摘编、问题解析七部分内容。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ')
