INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'392', N'商务英语写作（第2版）                                        ', N'《工商导论》该系列教材为商务英语专业、英语专业商务方向以及商科相关专业英语课程提供了比较完整的、成体系的教学资源。《工商导论》用英语全面、系统、概要地介绍了市场经济体制下工商企业如何运作，旨在帮助学生熟悉企业环境、经营管理、金融财务、法律保险等方面的英语词汇和用语，学会商务英语基本技能，为下一步学习更微观、更具体的课程打下基础。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ')
