INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'393', N'俄罗斯数学教材选译：连续介质力学1（第6版）                             ', N'《语文课程教学资源》分上、中、下三编。上编用三章概括地说明了课程资源的概念与分类，开发与利用课程资源的意义，以及树立明确的课程资源意识的内涵与重要性。中编六章是对语文课程的基本资源――语文教科书的分类研究，分别研究了语文教科书中的选文性质和类型、选文研究的目的和原则，还论述了如何研究语文教科书中的写实文章、文学作品和文言文三类选文，并举出实例阐释了写作教材、口语交际教材和综合性学习教材的研究内容与方法，其中特别强调了语文教师在研究语文教科书时，应该把选文作者的原创智慧、教科书编者的编制智慧和学生读者的读解智慧三者有机结合起来。下编三章是对语文课程相关资源的应用研究，分别说明了语文相关资源的教学价值、开发与利用语文课程相关资源的原则、途径和方法，尤其是其中选编和评点的多篇优秀案例，一定会使青年语文教师深感兴趣并深受教益。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ')
